// Bear and Blind Sorting, checker, Errichto
#include<bits/stdc++.h>
using namespace std;
typedef long double ld;
const int TESTS = 13; // including three samples

const int nax = 32;
ld p[nax][nax][nax*nax], ev[nax][nax][nax*nax];
ld p_finish_today, ev_finish_today;
ld p_start_again, ev_start_again;

bool isOk(int n, int m, ld r) {
	// read from the stdin
	for(int i = 0; i < nax; ++i) for(int j = 0; j < nax; ++j)
		for(int k = 0; k < nax * nax; ++k)
			p[i][j][k] = ev[i][j][k] = 0;
	p_finish_today = ev_finish_today = p_start_again = ev_start_again = 0;
	p[0][0][0] = 1;
	for(int i = 0; i <= n; ++i)
	for(int j = 0; j <= m; ++j)
	for(int k = 0; k <= i * j; ++k) {
		ld & my_p = p[i][j][k];
		ld & my_ev = ev[i][j][k];
		if(i == n && j == m) {
			p_finish_today += my_p;
			ev_finish_today += my_ev;
			continue;
		}
		ld omega = n * m;
		ld x;

		// staying in the same place
		x = k * (1 - r) / omega;
		my_ev += 1 / (1 - x) * my_p;
		omega -= k * (1 - r); // now we assume that a new dance will change sth

		// starting a new day
		x = k * r / omega;
		p_start_again += x * my_p;
		ev_start_again += x * (my_ev - my_p);

		// new boy
		x = (n-i) * j / omega;
		p[i+1][j][k+1] += x * my_p;
		ev[i+1][j][k+1] += x * my_ev;

		// new girl
		x = i * (m-j) / omega;
		p[i][j+1][k+1] += x * my_p;
		ev[i][j+1][k+1] += x * my_ev;

		// new both
		x = (n-i) * (m-j) / omega;
		p[i+1][j+1][k+1] += x * my_p;
		ev[i+1][j+1][k+1] += x * my_ev;

		// new pair with visited boy and visited girl
		x = (i * j - k) / omega;
		p[i][j][k+1] += x * my_p;
		ev[i][j][k+1] += x * my_ev;
	}
	ld correct = ev_finish_today / p_finish_today + ev_start_again / p_finish_today;
	double his;
	scanf("%lf", &his);
	const ld eps = 1e-6;
	return abs(his - correct) <= eps * max(correct, 1.0L);
}

int main() {
	srand(42);
	char dr[200];
	scanf("%s", dr);
	vector<bool> scores;
	for(int t = 0; t < TESTS; ++t) {
		char input[100], output[100];
		snprintf(input, sizeof(input), "%sinput%05d.in", dr, t);
		snprintf(output, sizeof(output), "%soutput%05d.out", dr, t);

		freopen(input, "r", stdin);
		int n, m;
		double r;
		scanf("%d%d%lf", &n, &m, &r);

		freopen(output, "r", stdin);
		scores.push_back(isOk(n, m, r));
	}
	int total = 0;
	for(int i = 3; i < (int) scores.size(); ++i)
		total += scores[i];
	printf("%lf\n", (double) total / (TESTS - 3));
	for(int rep = 0; rep < 2; ++rep)
		for(int i = 0; i < (int) scores.size(); ++i) {
			printf("%d", (int) scores[i]);
			if(i == (int) scores.size() - 1) puts("");
			else printf(" ");
		}
	return 0;
}
